pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

contract CommentContract {
    
  event AddComment(address recipient, uint commentId);
  event DeleteComment(uint commentId, bool isDeleted);

    struct Comment {
            uint id;
            address username;
            string commentText;
            bool isDeleted;
        }

    Comment[] private comments;

    // Mapping of Comment id to the wallet address of the user
    mapping(uint256 => address) commentToOwner;

    
// Method to be called by our frontend when trying to add a new Comment
    function addComment(string calldata commentText, bool isDeleted) external {
        uint commentId = comments.length;
        comments.push(Comment(commentId, msg.sender, commentText, isDeleted));
        commentToOwner[commentId] = msg.sender;
        emit AddComment(msg.sender, commentId);
    }

     // Method to get all the Comments
    function getAllComments() external view returns (Comment[] memory) {
        Comment[] memory temporary = new Comment[](comments.length);
        uint counter = 0;
        for(uint i=0; i<comments.length; i++) {
            if(comments[i].isDeleted == false) {
                temporary[counter] = comments[i];
                counter++;
            }
        }

        Comment[] memory result = new Comment[](counter);
        for(uint i=0; i<counter; i++) {
            result[i] = temporary[i];
        }
        return result;
    }

    // Method to get only your Comments
    function getMyComments() external view returns (Comment[] memory) {
        Comment[] memory temporary = new Comment[](comments.length);
        uint counter = 0;
        for(uint i=0; i<comments.length; i++) {
            if(commentToOwner[i] == msg.sender && comments[i].isDeleted == false) {
                temporary[counter] = comments[i];
                counter++;
            }
        }

        Comment[] memory result = new Comment[](counter);
        for(uint i=0; i<counter; i++) {
            result[i] = temporary[i];
        }
        return result;
    }

    // Method to Delete a Comment
    function deleteComment(uint commentId, bool isDeleted) external {
        if(commentToOwner[commentId] == msg.sender) {
            comments[commentId].isDeleted = isDeleted;
            emit DeleteComment(commentId, isDeleted);
        }
    }


}
